mapper.update:
update SYS_DICTIONARIES
set NAME = #{NAME},
ORDY_BY = #{ORDY_BY},
BIANMA = #{BIANMA}
where
ZD_ID = #{ZD_ID}

mapper.select:
select z1.ZD_ID, z1.NAME, z1.BIANMA, z1.ORDY_BY, z1.PARENT_ID, z1.JB, z2.NAME PNAME from SYS_DICTIONARIES z1 left join SYS_DICTIONARIES z2 on z1.PARENT_ID = z2.ZD_ID where z1.PARENT_ID = #{pd.PARENT_ID}
and z1.NAME LIKE CONCAT(CONCAT('%', #{pd.NAME}),'%')
order by z1.ORDY_BY

SELECT Z1.ZD_ID, Z1.NAME, Z1.BIANMA, Z1.ORDY_BY, Z1.PARENT_ID, Z1.JB, Z2.NAME PNAME FROM SYS_DICTIONARIES Z1 LEFT JOIN SYS_DICTIONARIES Z2 ON Z1.PARENT_ID = Z2.ZD_ID WHERE 1=1
AND Z1.ZD_ID = #{zd_id}
and z1.NAME LIKE CONCAT(CONCAT('%', #{name}),'%')
and z1.PARENT_ID = #{parent_id}
and Z2.NAME =#{pname}
and Z1.PARENT_ID in (SELECT ZD_ID FROM SYS_DICTIONARIES WHERE NAME=#{parent_name})
ORDER BY ORDY_BY ASC
