	//生成附件图片
 public static void BuildattAchment(List<Map<String, Object>> attachmentList,String imagespath){
	 try {
		 if (attachmentList.size() > 0 && attachmentList != null) {
			 File f = new File(imagespath);
			 if (!f.exists()) {
				 f.mkdirs();
			 }
			 for (Map<String, Object> map : attachmentList) {
				 String attachmentpath = map.get("ATTACHMENTPATH").toString(); // 图片的Base64码
				 String attachmentname = map.get("ATTACHMENTNAME").toString(); // 图片名字
				 File file = new File(imagespath + map.get("ATTACHMENTNAME").toString());
				 if (!file.exists()) {
					 Base64Image.getFileFromBase64String(attachmentpath, imagespath + attachmentname);
				 }
			 }
		 }
	 } catch (Exception e) {}
 }
	 
